export default {
  name: 'TreeMapChart',
  props: ['series'],
  computed: {
    getTheme() {
      if (this.$vuetify.theme.dark) {
        return 'dark';
      } else {
        return 'light';
      }
    },
    chartOptions() {
      return {
        colors: [this.$vuetify.theme.themes.light.primary, this.$vuetify.theme.themes.light.error],
        theme: {
          mode: this.getTheme
        },
        legend: {
          position: 'right'
        },
        tooltip: {
          theme: this.getTheme,
          y: {
            formatter(value) {
              var formatter = new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: 'USD'
              });
              return formatter.format(value);
            }
          }
        },
        chart: {
          toolbar: {
            show: false
          },
          background: 'none',
          animations: {
            enabled: false
          }
        }
      };
    }
  }
};
