import Vue from 'vue'
import Vuex from 'vuex'
import VuexPersistence from 'vuex-persist'
import axios from 'axios'

Vue.use(Vuex)

export default new Vuex.Store({
    plugins: [new VuexPersistence({
        storage: window.sessionStorage
    }).plugin],

    state: {
        isLoggedIn: false,

        userPrefs: {
            theme: 0,
            currency: 'USD'
        },

        notification: {
            text: '',
            color: ''
        },

        allAssets: [],
        allHistory: [],

        totalPositiveAssets: null,
        totalNegativeAssets: null,
    },

    getters: {

    },

    mutations: {
        login(state) {
            state.isLoggedIn = true
        },

        setUserPrefs(state, data) { state.userPrefs = data },

        logOut(state) {
            state.isLoggedIn = false

            state.userPrefs = {
                theme: 0,
                currency: 'USD'
            }
        },

        setNotification(state, data) { state.notification = data },

        setAllAssetData(state, data) {
            state.allAssets = data
        },

        setAllHistoryData(state, data) {
            state.allHistory = data
        },

        setTotalPositiveAssets(state, data) {
            state.totalPositiveAssets = data
        },

        setTotalNegativeAssets(state, data) {
            state.totalNegativeAssets = data
        },
    },

    actions: {
        async getAllAssetData() {
            // Get raw asset data
            axios.get(`http://localhost:3000/api/assets/all`)
            .then(resp => {
                let allAssets = resp.data
                
                // Set total values
                if (allAssets.length) {
                    let totalPositiveAssets = 0
                    let totalNegativeAssets = 0

                    for (let asset of allAssets) {
                        if (!asset.is_deleted) {
                            if (asset.is_debt) {
                                totalNegativeAssets += parseFloat(asset.value)
                            }
                            else {
                                totalPositiveAssets += parseFloat(asset.value)
                            }
                        }
                    }
                    
                    this.commit('setTotalPositiveAssets', totalPositiveAssets)
                    this.commit('setTotalNegativeAssets', totalNegativeAssets)
                    this.commit('setAllAssetData', allAssets)
                }

                axios.get(`http://localhost:3000/api/assets/history`)
                .then(resp => {
                    let rawHistory = resp.data

                    // Get all individual assets
                    let assetList = []
                    for (let asset of allAssets) {
                        assetList.push({
                            id: asset.id,
                            history: []
                        })
                    }

                    // Get all dates that there are records for
                    let uniqueDates = []
                    for (let entry of rawHistory) {
                        if (!uniqueDates.includes(entry.date)) {
                            uniqueDates.push(entry.date)
                        }
                    }
                    uniqueDates = uniqueDates.sort()

                    // Go through every asset
                    for (let asset of assetList) {
                        for (let date of uniqueDates) {                    
                            // Check if there is any value for that asset on that date, add it if there is.
                            for (let entry of rawHistory) {
                                if (entry.date == date && entry.asset_id == asset.id) {
                                    asset.history.push({
                                        x: entry.date,
                                        y: parseFloat(entry.value)
                                    })
                                }
                            }
                            // If the asset doesn't have an entry for a date with data, add one with the previous value.
                            if (!asset.history.some(e => e.x == date)) {
                                if (asset.history.length) {
                                    asset.history.push({
                                        x: date,
                                        y: parseFloat(asset.history[asset.history.length - 1].y)
                                    })
                                }
                            }
                        }
                    }

                    // Turn the asset data into something the line chart can read
                    for (let asset of assetList) {
                        for (let entry of asset.history) {
                            let i = allAssets.history.findIndex(e => e.x == entry.x)
                            if (i < 0) {
                                allAssets.history.push(entry)
                            }
                            else {
                                allAssets.history[i].y += entry.y
                            }
                        }
                    }

                    this.commit('setAssetData', allAssets)
                })
            })
            .catch(() => {
                this.$router.push('/404')
            })
        },

        async getDebtData() {
            // Set up object we want to return
            let debts = {
                raw: [],
                totalValue: 0,
                tableData: [],
                pieChartValues: [],
                pieChartLabels: [],
                history: [],
            }

            // Get raw asset data
            axios.get(`http://localhost:3000/api/debts`)
            .then(resp => {
                debts.raw = resp.data
                
                // Set total value and pie chart data
                if (debts.raw.length) {
                    for (let debt of debts.raw) {
                        if (!debt.is_deleted) {
                            debts.pieChartLabels.push(debt.name)
                            debts.pieChartValues.push(parseFloat(debt.value))
                            debts.totalValue += parseFloat(debt.value)
                        }
                    }
                }

                // Filter out deleted assets in the table
                debts.tableData = debts.raw.filter(e => e.is_deleted == 0)

                axios.get(`http://localhost:3000/api/debts/history`)
                .then(resp => {
                    let rawHistory = resp.data

                    // Get all individual assets
                    let debtList = []
                    for (let debt of debts.raw) {
                        debtList.push({
                            id: debt.id,
                            history: []
                        })
                    }

                    // Get all dates that there are records for
                    let uniqueDates = []
                    for (let entry of rawHistory) {
                        if (!uniqueDates.includes(entry.date)) {
                            uniqueDates.push(entry.date)
                        }
                    }
                    uniqueDates = uniqueDates.sort()

                    // Go through every asset
                    for (let debt of debtList) {
                        for (let date of uniqueDates) {                    
                            // Check if there is any value for that asset on that date, add it if there is.
                            for (let entry of rawHistory) {
                                if (entry.date == date && entry.asset_id == debt.id) {
                                    debt.history.push({
                                        x: entry.date,
                                        y: parseFloat(entry.value)
                                    })
                                }
                            }
                            // If the asset doesn't have an entry for a date with data, add one with the previous value.
                            if (!debt.history.some(e => e.x == date)) {
                                if (debt.history.length) {
                                    debt.history.push({
                                        x: date,
                                        y: parseFloat(debt.history[debt.history.length - 1].y)
                                    })
                                }
                            }
                        }
                    }

                    // Turn the asset data into something the line chart can read
                    for (let debt of debtList) {
                        for (let entry of debt.history) {
                            let i = debts.history.findIndex(e => e.x == entry.x)
                            if (i < 0) {
                                debts.history.push(entry)
                            }
                            else {
                                debts.history[i].y += entry.y
                            }
                        }
                    }

                    this.commit('setDebtData', debts)
                })
            })
            .catch(() => {
                this.$router.push('/404')
            })
        },
    }
})
